mapper.select:
SELECT tci.case_id, tci.summary, tci.folder_id, tci.project_id, tci.case_num, tci.created_by, tci.last_updated_by, tci.creation_date, tci.last_update_date, tci.object_version_number FROM ( SELECT tc.case_id, tc.summary, tc.folder_id, tc.project_id, CONCAT_WS( '-', tpi.project_code, tc.case_num ) as case_num, tc.created_by, tc.last_updated_by, tc.creation_date, tc.last_update_date, tc.object_version_number FROM test_case tc left join test_project_info tpi on tpi.project_id = tc.project_id WHERE tc.project_id = #{projectId} AND tc.folder_id IN
#{folderId}
) as tci WHERE
order by tci.last_update_date DESC

SELECT tci.case_id FROM ( SELECT tc.case_id, tc.summary, tc.folder_id, tc.project_id, CONCAT_WS( '-', tpi.project_code, tc.case_num ) as case_num, tc.created_by, tc.last_updated_by, tc.creation_date, tc.last_update_date, tc.object_version_number FROM test_case tc right join test_project_info tpi on tpi.project_id = tc.project_id WHERE tc.project_id = #{projectId} AND tc.folder_id IN
#{folderId}
) as tci
order by tci.last_update_date DESC

SELECT * FROM test_case WHERE project_id = #{projectId} AND case_id IN
#{caseId}
order by case_id DESC

SELECT tc.case_id as case_id, tc.summary as summary, tc.description as description, tif.`name` as folderName, cs.step_id as step_id, cs.test_step as test_step, cs.test_data as test_data, cs.rank as rank, cs.expected_result as expected_result, tca.file_name, tca.url FROM test_case tc left join test_case_step cs on cs.issue_id = tc.case_id left join test_issue_folder tif on tif.folder_id = tc.folder_id left join test_case_attachment tca on tca.case_id = tc.case_id left join test_cycle_case tcase on tcase.case_id = tc.case_id where tc.case_id in
#{caseId}
and tcase.execute_id in
#{executedId}
ORDER BY tc.case_id

