mapper.select:
SELECT cycle.execute_step_id,cycle.execute_id,cycle.step_id,cycle.comment,cycle.object_version_number, attach1.url,attach1.attachment_name,attach1.id,test.test_step,test.test_data,test.expected_result, test.case_attach_url,test.case_attach_id,test.case_attach_name FROM test_cycle_case_step cycle left join ( SELECT step.step_id,step.rank,step.issue_id,step.test_step,step.test_data,step.expected_result,attach.url case_attach_url,attach.attachment_name case_attach_name,attach.id case_attach_id FROM test_case_step step left join test_cycle_case_attachment_rel attach on step.step_id=attach.attachment_link_id and attach.attachment_type="CASE_STEP" ) test on cycle.step_id=test.step_id left join test_cycle_case_attachment_rel attach1 on cycle.execute_step_id=attach1.attachment_link_id and attach1.attachment_type="CYCLE_STEP"
AND cycle.execute_step_id = #{executeStepId}
AND cycle.execute_id = #{executeId}
order by test.rank
